import React, { useEffect, useRef, useState } from "react";
import Navbar from "./Navbar";
import Footer from "./Footer";
import HOC from "../img/HPC.jpg";
import "./Conference.css";

const conferences = [
  {
    id: 1,
    year: "2024",
    month: "December",
    title: "MoroccoAI Annual Conference '24",
    image: HOC,
    description: "Driving the Future of Innovation Through AI.",
    date: "December 4-5, 2024",
    link: "#",
  },
  {
    id: 2,
    year: "2023",
    month: "December",
    title: "MoroccoAI Annual Conference '23",
    image: HOC,
    description: "Reshaping Work in the Era of AI.",
    date: "December 20-21, 2023",
    link: "#",
  },
  {
    id: 3,
    year: "2023",
    month: "August",
    title: "AI Summer School",
    image: HOC,
    description: "Explore cutting-edge AI topics and innovations.",
    date: "August 15-20, 2023",
    link: "#",
  },
];

function Conference() {
  const [animatedCards, setAnimatedCards] = useState([]);
  const cardRefs = useRef([]);

  useEffect(() => {
    cardRefs.current = cardRefs.current.slice(0, conferences.length);

    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          const index = entry.target.getAttribute("data-index");
          if (entry.isIntersecting && !animatedCards.includes(index)) {
            setAnimatedCards((prev) => [...prev, index]);
          }
        });
      },
      { threshold: 0.1 }
    );

    cardRefs.current.forEach((ref) => {
      if (ref) observer.observe(ref);
    });

    return () => {
      cardRefs.current.forEach((ref) => {
        if (ref) observer.unobserve(ref);
      });
    };
  }, [animatedCards]);

  return (
    <div className="bg-gradient-to-b from-gray-800 to-black flex flex-col min-h-screen">
      <Navbar />

      {/* Title Section */}
      <div className="text-center py-32 px-6">
        <h1 className="text-5xl font-bold text-white mb-4 animate-fadeInUp">
          Morocco <span className="text-blue-600">HPC</span> Conferences
        </h1>
        <p
          className="text-white mt-6 max-w-4xl mx-auto animate-fadeInUp text-lg sm:text-xl md:text-2xl"
          style={{ animationDelay: "0.4s" }}
        >
          HPC events bring together researchers, students, and industry leaders
          to explore, discuss, and advance knowledge in High-Performance
          Computing, fostering innovation and collaboration across various
          scientific and industrial fields.
        </p>
      </div>

      {/* Timeline */}
      <div className="flex-grow relative mt-16 p-7">
        {/* Vertical Line */}
        <div className="absolute left-1/2 transform -translate-x-1/2 h-full border-l-4 border-white"></div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-12">
          {conferences.map((conf, index) => {
            const isAnimated = animatedCards.includes(index.toString());
            return (
              <div
                key={conf.id}
                className={`relative flex ${
                  index % 2 === 0 ? "justify-start" : "justify-end"
                }`}
              >
                {/* Conference Card */}
                <div
                  className={`w-full transform transition-all duration-700 ${
                    isAnimated ? "opacity-100 animate-fadeInUp" : "opacity-0"
                  }`}
                  data-index={index}
                  ref={(el) => (cardRefs.current[index] = el)}
                  style={{ animationDelay: `${index * 0.2}s` }}
                >
                  <div className="bg-white rounded-xl shadow-lg overflow-hidden transform hover:scale-105 hover:shadow-2xl transition duration-300">
                    <img
                      src={conf.image}
                      alt={conf.title}
                      className="w-full h-48 object-cover rounded-t-xl"
                    />
                    <div className="p-6">
                      <h2 className="text-2xl font-semibold text-blue-600 mb-3">
                        {conf.title}
                      </h2>
                      <p className="text-gray-600 mb-3">{conf.description}</p>
                      <p className="text-gray-700 font-semibold text-sm">
                        {conf.date}
                      </p>
                      <a
                        href={conf.link}
                        className="inline-block text-blue-600 hover:text-blue-800 font-semibold mt-3 text-lg"
                      >
                        Learn more &rarr;
                      </a>
                    </div>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>

      <Footer />
    </div>
  );
}

export default Conference;
