import React, { useEffect, useRef, useState } from "react";
import Navbar from "./Navbar";
import Footer from "./Footer";
import WalidImage from "../img/H-M.jpg"; // Replace with actual image path
import OuailImage from "../img/O-M.jpg"; // Replace with actual image path
import SafaeImage from "../img/safae.jpg"; // Replace with actual image path

function About() {
  const [visibleCards, setVisibleCards] = useState([]);
  const cardRefs = useRef([]);

  const cards = [
    {
      id: 1,
      name: "Walid Houmaidi",
      delay: "0s",
      desc: "Developer, Al Akhawayn University Student",
      img: WalidImage,
    },
    {
      id: 2,
      name: "Ouail El Maadi",
      delay: "0.2s",
      desc: "Developer, Al Akhawayn University Student",
      img: OuailImage,
    },
    {
      id: 3,
      name: "Dr. Safae Bourhnane",
      delay: "0.4s",
      desc: "Supervisor",
      img: SafaeImage,
    },
  ];

  useEffect(() => {
    // Set up Intersection Observer
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          const index = entry.target.getAttribute("data-index");
          if (entry.isIntersecting && !visibleCards.includes(index)) {
            setVisibleCards((prev) => [...prev, index]);
          }
        });
      },
      { threshold: 0.1 } // Adjust threshold as needed
    );

    // Observe each card
    cardRefs.current.forEach((ref) => {
      if (ref) observer.observe(ref);
    });

    return () => {
      cardRefs.current.forEach((ref) => {
        if (ref) observer.unobserve(ref);
      });
    };
  }, [visibleCards]);

  return (
    <div className="min-h-screen bg-black flex flex-col">
      {/* Navbar */}
      <Navbar />

      {/* Content Container with extra top margin to avoid navbar overlap */}
      <div className="flex-grow max-w-5xl mx-auto px-4 py-16 mt-24">
        {/* Introduction Paragraph */}
        <div className="mb-12 text-center">
          <h1 className="text-3xl font-bold text-white mb-4">About The Team</h1>
          <p className="text-gray-300 leading-relaxed">
            This application was developed by{" "}
            <span className="font-semibold text-white">Walid Houmaidi</span> and{" "}
            <span className="font-semibold text-white">Ouail El Maadi</span>, under the
            supervision of <span className="font-semibold text-white">Dr. Safae Bourhnane</span>.
            Their collaborative effort and guidance ensured a high-quality product that aims to
            serve its users effectively.
          </p>
        </div>

        {/* Team Section */}
        <div className="space-y-8">
          {cards.map((card, index) => {
            const isVisible = visibleCards.includes(index.toString());
            return (
              <div
                key={card.id}
                data-index={index}
                ref={(el) => (cardRefs.current[index] = el)}
                className={`rounded-lg shadow p-6 flex flex-col items-center text-white transform transition-transform duration-500
                ${isVisible ? "animate-fadeInUp hover:scale-105" : "opacity-0"}`}
                style={{ animationDelay: card.delay }}
              >
                <div className="rounded-full border-4 border-blue-600 w-32 h-32 overflow-hidden mb-4">
                  <img
                    src={card.img}
                    alt={card.name}
                    className="w-full h-full object-cover"
                  />
                </div>
                <h2 className="text-xl font-semibold">{card.name}</h2>
                <p className="mt-2 text-center">{card.desc}</p>
              </div>
            );
          })}
        </div>
      </div>

      {/* Footer */}
      <Footer />
    </div>
  );
}

export default About;
