useEffect(() => {
  const upcomingStr = "UPCOMING";
  const webinarStr = "WEBINAR";
  let upcomingIndex = 0;
  let webinarIndex = 0;

  const upcomingInterval = setInterval(() => {
    if (upcomingIndex < upcomingStr.length) {
      setUpcomingText((prev) => prev + upcomingStr[upcomingIndex]);
      upcomingIndex++;
    } else {
      clearInterval(upcomingInterval);

      // Start typing "WEBINAR"
      const webinarInterval = setInterval(() => {
        if (webinarIndex < webinarStr.length) {
          setWebinarText((prev) => prev + webinarStr[webinarIndex]);
          webinarIndex++;
        } else {
          clearInterval(webinarInterval); // Stop after finishing "WEBINAR"
        }
      }, 100);
    }
  }, 100);

  return () => {
    clearInterval(upcomingInterval);
  };
}, []);
